import React, {useEffect, useState} from "react";
import {DataGrid, ThemeProvider, createTheme} from "@mui/x-data-grid";
import axios from "axios";

const theme = createTheme(); // Create a theme instance

const columns = [
  {field: "match_id", headerName: "Match ID", width: 130},
  {field: "player_A", headerName: "Player A", width: 130},
  {field: "player_B", headerName: "Player B", width: 130},
  {field: "tourney_name", headerName: "Tourney Name", width: 130},
  {field: "tourney_level", headerName: "Tourney Level", width: 130},
  {field: "surface", headerName: "Surface", width: 130},
  {field: "round", headerName: "Round", width: 130},
  {field: "A_elo", headerName: "A Elo", type: "number", width: 130},
  {field: "B_elo", headerName: "B Elo", type: "number", width: 130},
];

function App() {
  const [rows, setRows] = useState([]);

  // useEffect(() => {
  //   const params = {
  //     player_name: "PlayerName", // replace with actual player name
  //     tourney_level: "Level", // replace with actual tourney level
  //     surface: "Surface", // replace with actual surface
  //   };

  //   axios.get("http://localhost:5000/matches", {params}).then((response) => {
  //     const matches = response.data;
  //     setRows(matches);
  //   });
  // }, []);

  useEffect(() => {
    // Placeholder data
    const placeholderMatches = [
      {
        match_id: 1,
        player_A: "Player A",
        player_B: "Player B",
        tourney_name: "Tournament 1",
        tourney_level: "Level 1",
        surface: "Grass",
        round: "Final",
        A_elo: 2000,
        B_elo: 1800,
      },
      // Add more placeholder matches as needed
    ];

    // Simulate an asynchronous delay to mimic API/database response time
    const delay = setTimeout(() => {
      setRows(placeholderMatches);
    }, 1000);

    // Clean up the timeout when the component is unmounted or the dependency changes
    return () => clearTimeout(delay);
  }, []);

  return (
    <div style={{height: 400, width: "100%"}}>
      <DataGrid rows={rows} columns={columns} pageSize={5} checkboxSelection />
    </div>
  );
}

export default function ThemedApp() {
  return (
    <ThemeProvider theme={theme}>
      <App />
    </ThemeProvider>
  );
}
